`timescale 1ns / 1ps

module draw_module(
	input wire clk_pix,
	input win, lose, draw,
	input wire [1023:0] mmap_hour_10s, mmap_hour_1s, mmap_min_10s, mmap_min_1s, 
                        mmap_sec_10s, mmap_sec_1s, mmap_c_tens, mmap_c_ones,
                        mmap_frac, mmap_dealer_tens, mmap_dealer_ones, mmap_user_tens,
                        mmap_user_ones,
	input wire [9:0] sx,
	input wire [9:0] sy,
	input wire de,
	input wire hsync,
	input wire vsync,
	output reg vga_hs,
	output reg vga_vs,
	output reg [3:0] vga_r,
	output reg [3:0] vga_g,
	output reg [3:0] vga_b
    );
    //make draw signals 32*32 / 16*32
    reg [1023:0] mmap_dot_c = 1024';
    reg [511:0] mmap_dot = 512';
    reg [511:0] mmap_clock_dot = 512';
    
    reg [1023:0] E = 1024';
    reg [1023:0] R = 1024';
    reg [1023:0] U = 1024';
    reg [1023:0] A = 1024';    
    reg [1023:0] S = 1024';
    reg [1023:0] D = 1024';
    reg [1023:0] J = 1024';
    reg [1023:0] K = 1024';
    reg [1023:0] L = 1024';
    reg [1023:0] C = 1024';
    reg [1023:0] B = 1024';
    
    
    wire q_draw;
    assign q_draw = 
    ((sx >= 416 && sx <= 447) && (sy >= 0 && sy <= 31)) ? mmap_hour_10s[32*(32-sy+0)-1-sx+416] : //시
    ((sx >= 448 && sx <= 479) && (sy >= 0 && sy <= 31)) ? mmap_hour_1s[32*(32-sy+0)-1-sx+448] : //시
    ((sx >= 480 && sx <= 495) && (sy >= 0 && sy <= 31)) ? mmap_clock_dot[16*(32-sy+0)-1-sx+480] : //:
    ((sx >= 496 && sx <= 527) && (sy >= 0 && sy <= 31)) ? mmap_min_10s[32*(32-sy+0)-1-sx+496] :  //분
    ((sx >= 528 && sx <= 559) && (sy >= 0 && sy <= 31)) ? mmap_min_1s[32*(32-sy+0)-1-sx+528] : //분
    ((sx >= 560 && sx <= 575) && (sy >= 0 && sy <= 31)) ? mmap_clock_dot[16*(32-sy+0)-1-sx+560] :  //:
    ((sx >= 576 && sx <= 607) && (sy >= 0 && sy <= 31)) ? mmap_sec_10s[32*(32-sy+0)-1-sx+576] : //초
    ((sx >= 608 && sx <= 639) && (sy >= 0 && sy <= 31)) ? mmap_sec_1s[32*(32-sy+0)-1-sx+608] :  //초
    
    ((sx >= 496 && sx <= 527) && (sy >= 32 && sy <= 63)) ? mmap_c_tens[32*(32-sy+32)-1-sx+496] : //온
    ((sx >= 528 && sx <= 559) && (sy >= 32 && sy <= 63)) ? mmap_c_ones[32*(32-sy+32)-1-sx+528] : //도
    ((sx >= 560 && sx <= 575) && (sy >= 32 && sy <= 63)) ? mmap_dot[16*(32-sy+32)-1-sx+560] : //.
    ((sx >= 576 && sx <= 607) && (sy >= 32 && sy <= 63)) ? mmap_frac[32*(32-sy+32)-1-sx+576] : //소수점
    ((sx >= 608 && sx <= 639) && (sy >= 32 && sy <= 63)) ? mmap_dot_c[32*(32-sy+32)-1-sx+608] : //'C
    
    ((sx >= 176 && sx <= 207) && (sy >= 128 && sy <= 159)) ? B[32*(32-sy+128)-1-sx+176] : //B
    ((sx >= 208 && sx <= 239) && (sy >= 128 && sy <= 159)) ? L[32*(32-sy+128)-1-sx+208] : //L
    ((sx >= 240 && sx <= 271) && (sy >= 128 && sy <= 159)) ? A[32*(32-sy+128)-1-sx+240] : //A
    ((sx >= 272 && sx <= 303) && (sy >= 128 && sy <= 159)) ? C[32*(32-sy+128)-1-sx+272] : //C
    ((sx >= 304 && sx <= 335) && (sy >= 128 && sy <= 159)) ? K[32*(32-sy+128)-1-sx+304] : //K
    ((sx >= 336 && sx <= 367) && (sy >= 128 && sy <= 159)) ? J[32*(32-sy+128)-1-sx+336] : //J
    ((sx >= 368 && sx <= 399) && (sy >= 128 && sy <= 159)) ? A[32*(32-sy+128)-1-sx+368] : //A
    ((sx >= 400 && sx <= 431) && (sy >= 128 && sy <= 159)) ? C[32*(32-sy+128)-1-sx+400] : //C
    ((sx >= 432 && sx <= 465) && (sy >= 128 && sy <= 159)) ? K[32*(32-sy+128)-1-sx+432] : //K
    
    ((sx >= 96  && sx <= 127) && (sy >= 224 && sy <= 255)) ? D[32*(32-sy+224)-1-sx+96]  : //D
    ((sx >= 128 && sx <= 159) && (sy >= 224 && sy <= 255)) ? E[32*(32-sy+224)-1-sx+128] : //E
    ((sx >= 160 && sx <= 191) && (sy >= 224 && sy <= 255)) ? A[32*(32-sy+224)-1-sx+160] : //A
    ((sx >= 192 && sx <= 223) && (sy >= 224 && sy <= 255)) ? L[32*(32-sy+224)-1-sx+192] : //L
    ((sx >= 224 && sx <= 255) && (sy >= 224 && sy <= 255)) ? E[32*(32-sy+224)-1-sx+224] : //E
    ((sx >= 256 && sx <= 287) && (sy >= 224 && sy <= 255)) ? R[32*(32-sy+224)-1-sx+256] : //R
    
    ((sx >= 384 && sx <= 415) && (sy >= 224 && sy <= 255)) ? U[32*(32-sy+224)-1-sx+384] : //U
    ((sx >= 416 && sx <= 447) && (sy >= 224 && sy <= 255)) ? S[32*(32-sy+224)-1-sx+416] : //S
    ((sx >= 448 && sx <= 479) && (sy >= 224 && sy <= 255)) ? E[32*(32-sy+224)-1-sx+448] : //E
    ((sx >= 480 && sx <= 511) && (sy >= 224 && sy <= 255)) ? R[32*(32-sy+224)-1-sx+480] : //R
    
    ((sx >= 160 && sx <= 191) && (sy >= 256 && sy <= 287)) ? mmap_dealer_tens[32*(32-sy+256)-1-sx+160] : //DEALER SCORE
    ((sx >= 192 && sx <= 223) && (sy >= 256 && sy <= 287)) ? mmap_dealer_ones[32*(32-sy+256)-1-sx+192] : 
    
    ((sx >= 416 && sx <= 447) && (sy >= 256 && sy <= 287)) ? mmap_user_tens[32*(32-sy+256)-1-sx+416] : //USER SCORE
    ((sx >= 448 && sx <= 479) && (sy >= 256 && sy <= 287)) ? mmap_user_ones[32*(32-sy+256)-1-sx+448] : 

    0;

                    
    //drive display
    always @(posedge clk_pix)
    begin
        vga_hs <= hsync;
        vga_vs<= vsync;
        vga_r<= de ? (q_draw ? 4'h0 : win ? 4'hC : lose ? 4'hF : draw ? 4'h0 : 4'hF) : 0;
	    vga_g<= de ? (q_draw ? 4'h0 : win ? 4'hC : lose ? 4'h3 : draw ? 4'h0 : 4'hF) : 0;
	    vga_b<= de ? (q_draw ? 4'h0 : win ? 4'h0 : lose ? 4'h3 : draw ? 4'h9 : 4'hF) : 0;
    end
    
endmodule