module vga_decoder(
    input wire [3:0] data,
    output reg [1023:0] data_out
    );
    
    always @(*) begin
        case(data)
            0: data_out <= 1024';
            1: data_out <= 1024';
            2: data_out <= 1024';
            3: data_out <= 1024';
            4: data_out <= 1024';
            5: data_out <= 1024';
            6: data_out <= 1024';
            7: data_out <= 1024';
            8: data_out <= 1024';
            9: data_out <= 1024';
            default: data_out <= 0;
        endcase
    end
endmodule
